var MCatCommon = require( 'message-catalog/common' ).MCatCommon;

exports.MCat = new MCat();

function MCat () {
  this.badValueMsg       = 'not a valid value';
  this.emulatePrefix     = 'Emulate: ';
  this.instancePrefix    = 'Instance: ';
  this.nonAdminWarning   = 'no connection added; only admins can emulate';
  this.passwordPrefix    = 'Password: ';

  for ( a in MCatCommon) {
    if ( a in this ) { continue; }
    this[a] = MCatCommon[a];
  }

}
