var MCatCommon = require( 'message-catalog/common' ).MCatCommon;

exports.MCat = new MCat();

function MCat () {
  this.connectMsg         = 'connecting...';
  this.failedSignInMsg    = 'could not sign in';
  this.networkError       = 'check the login details, host or network';
  this.userDetailsAction  = 'read user details';
  this.userDetailsMsg     = 'retrieved user details';

  for ( a in MCatCommon) {
    if ( a in this ) { continue; }
    this[a] = MCatCommon[a];
  }

}
