var MCatCommon = require( 'message-catalog/common' ).MCatCommon;

exports.MCat = new MCat();

function MCat () {
  this.connect           = 'connecting...';
  this.failedSignIn      = 'could not sign in';
  this.userDetailsAction = 'read user details';
  this.userDetails       = 'retrieved user details';

  for ( a in MCatCommon) {
    if ( a in this ) { continue; }
    this[a] = MCatCommon[a];
  }

}
