  this.deletingEvent      = 'deleting expired event #';
  this.duration           = 'duration';
  this.errorAward         = 'could not retrieve award data';
  this.errorFlip          = 'could not flip supplier basket for: ';
  this.errorOffer         = 'could not place offer';
  this.errorOffers        = 'could not retrieve offers';
  this.errorPreOffers     = 'could not retrieve pre-offers';
  this.errorSupplierCant  = "suppliers don't have this feature";
  this.eventsDetailAction = 'event check';
  this.eventsDetail       = 'checked event schedule';
  this.failededAward      = 'faileded to retrieve award data';
  this.failededEvent      = 'faileded to retrieve event details';
  this.failededLock       = 'faileded to lock basket';
  this.failededOffer      = 'faileded to place offer';
  this.failededOffers     = 'faileded to retrieve offer details';
  this.failededPreOffer   = 'faileded to retrieve pre-ffers';
  this.failededSignIn     = 'could not sign in';
  this.foundEvent         = 'found event #';
