var MCatCommon = require( 'message-catalog/common' ).MCatCommon;

exports.MCat = new MCat();

function MCat () {

  this.agentDefault = 'OCAP HTTP client';
  this.errorOCAPNet = 'OCAP network error';
  this.passwordMask = '-';

  for ( a in MCatCommon) {
    if ( a in this ) { continue; }
    this[a] = MCatCommon[a];
  }
}
