var Auth    = require( 'ocap/auth.js' ).Auth,
    Event   = require( 'ocap/event.js' ).Event,
    Invoice = require( 'ocap/invoice.js' ).Invoice,
    Net     = require( 'ocap/net.js' ).Net;

exports.Library = Library;

function Library( userAgent, finishHandler, emitter, pageSize ) {

  var self = this;
  self.connectionCount = 1;
  self.net     = new Net( userAgent, emitter );
  self.auth    = new Auth( self.net,  finishHandler, emitter );
  self.event   = new Event( self.net,  finishHandler, emitter, pageSize );
  self.invoice = new Invoice( self.net,  finishHandler, emitter, pageSize );

}
